/**
 * OTP Core Script Handler
 * 
 * Serves the bundled OtpLoginCore script for browser use
 * Similar to decrypt-script.js but for OTP authentication
 */

// Import the bundled script (embedded at build time)
// This is generated by scripts/bundle-otp-core.js
// For now, we'll read it from disk at runtime (can be optimized later)
import { readFileSync } from 'fs';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

let cachedScript = null;

/**
 * Serve bundled OTP core script
 * GET /otp-core.js
 */
export async function handleOtpCoreScript() {
  try {
    // Cache the script in memory (it's static)
    if (!cachedScript) {
      const scriptPath = join(__dirname, '../dist/otp-core.js');
      cachedScript = readFileSync(scriptPath, 'utf-8');
    }

    return new Response(cachedScript, {
      headers: {
        'Content-Type': 'application/javascript; charset=utf-8',
        'Cache-Control': 'public, max-age=3600',
      },
    });
  } catch (error) {
    console.error('Failed to serve OTP core script:', error);
    return new Response('// OTP core script not found. Please run: pnpm build:otp-core', {
      status: 404,
      headers: { 'Content-Type': 'application/javascript' },
    });
  }
}

