/**
 * Handler for serving the bundled OTP Login Svelte component
 * 
 * Serves the pre-built Svelte component bundle for use in standalone HTML
 */

// Import the bundled script (embedded at build time)
// This is generated by scripts/bundle-otp-login-svelte.js
import { BUNDLED_OTP_LOGIN_SVELTE_SCRIPT } from '../dist/otp-login-svelte-script.js';

export async function handleOtpLoginSvelteScript() {
  return new Response(BUNDLED_OTP_LOGIN_SVELTE_SCRIPT, {
    headers: {
      'Content-Type': 'application/javascript; charset=utf-8',
      'Cache-Control': 'public, max-age=3600',
    },
  });
}

